import { Link } from "react-router-dom";
import { Tooltip, Form, Input, Button } from "@arco-design/web-react";
import { useState, createContext, useContext } from "react";
import axios from "axios";
import { useStateProvider } from "./providers/StateProvider";

const FormItem = Form.Item;

function InteractPage ()  {
    const UserContext = createContext();
    const user = useContext(UserContext);
    const [form] = Form.useForm();
    const [quote, setQuote] = useState('boohoo')
    const [Id, setId] = useState('null/');
    const { state, setState } = useStateProvider();
    const onSubmit = async () => {
        const { Color, Clothing } = await form.validate();
        setQuote(
          `Your favorite color is ${Color}, favorite Clothing is ${Clothing}`
        );
        form.resetFields()
        // api对接
      };

      const getQuote = () => {
        axios.get("http://localhost:8000/api/userinfo/" + Id).then(
          (response) => {
              setQuote(response.data["weather"])
          }).catch(err => {
            console.log(err)
          })
      }

    return (
        <div
            style={{
                justifyContent: "center",
                alignItems: "center",
                backgroundColor: "#f2f9ff",
            }}
        >
            <Form
          form={form}
          style={{ width: 600, margin: "auto" }}
          autoComplete="off"
          onSubmit={onSubmit}
        >
        <h2>{`Hello ${state} again!`}</h2>
          <FormItem label="Color" field="Color" rules={[{ required: true }]}>
            <Input placeholder="please enter your favorite Color..." />
          </FormItem>
          <FormItem
            label="Clothing"
            field="Clothing"
            rules={[{ required: true }]}
          >
            <Input placeholder="please enter your favorite Clothing..." />
          </FormItem>
          <FormItem wrapperCol={{ offset: 5 }} rules={[{ required: true }]}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </FormItem>
        </Form>
            Interact Page
            <Link to="/">
                Back to Main Page
            </Link>
        </div>
    );
}

export default InteractPage;
